INSERT INTO `article` VALUES (1,'Khai thác dịch vụ du thuyền: Xu hướng mới của các công ty du lịch',NULL,'<p style=\"text-align:justify\"><strong><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Kh&aacute;ch du lịch hiện nay đ&atilde; c&oacute; nhiều sự lựa chọn về phương tiện hơn khi muốn kh&aacute;m ph&aacute; vẻ đẹp của một mảnh đất mới. Họ c&oacute; thể đi bằng đường bộ, h&agrave;ng kh&ocirc;ng v&agrave; thậm ch&iacute; tr&ecirc;n cả những du thuyền sang trọng.</span></span></span></strong><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"Khai thác dịch vụ du thuyền: Xu hướng mới của các công ty du lịch\" src=\"http://www.dulich.vn/FileManager/vietpro2203/duthuyentrenvinhhalong-Copy-1.jpg\" style=\"height:267px; margin:0px; width:434px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n<br />\r\n<br />\r\n<em>Du thuyền tr&ecirc;n Vịnh Hạ Long. (Ảnh: Tiến Dũng/TTXVN)</em>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Xu hướng được ngắm&nbsp;cảnh đẹp thi&ecirc;n nhi&ecirc;n th&ocirc;ng qua trải nghiệm tr&ecirc;n những chiếc du thuyền sang trọng đang được nhiều du kh&aacute;ch lựa chọn những th&aacute;ng đầu năm 2016.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Nhận định n&agrave;y được đưa ra bởi một số c&aacute;c c&ocirc;ng ty du lịch khi đề cập tới vấn đề khai th&aacute;c dịch vụ du thuyền.<br />\r\n<br />\r\n<strong>M&atilde;n nh&atilde;n với kh&aacute;ch sạn 5 sao nổi</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">&ldquo;M&igrave;nh cảm thấy dịch vụ du lịch bằng du thuyền vừa thuận lợi lại tiết kiệm nhiều thời gian. Bởi gia đ&igrave;nh m&igrave;nh c&oacute; thể ngắm vẻ đẹp của cả 3 đất nước: Trung Quốc, H&agrave;n Quốc v&agrave; Nhật Bản ngay tr&ecirc;n chiếc du thuyền m&agrave; kh&ocirc;ng mất nhiều thời gian dịch chuyển phương tiện. Với m&igrave;nh, ưu điểm n&agrave;y l&agrave; đ&aacute;ng kể bởi gia đ&igrave;nh rất kh&oacute; sắp xếp thời gian để cả nh&agrave; c&ugrave;ng đi chơi,&rdquo; chị V&acirc;n (Th&agrave;nh phố Hồ Ch&iacute; Minh) gi&atilde;i b&agrave;y khi n&oacute;i đến những tiện &iacute;ch th&ocirc;ng qua trải nghiệm du thuyền cao cấp.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">C&ugrave;ng suy nghĩ như chị V&acirc;n, nhiều du kh&aacute;ch kh&aacute;c khi trải nghiệm du lịch bằng du thuyền cao cấp cũng x&oacute;a bỏ lo ngại ban đầu về độ an to&agrave;n. Bởi kh&aacute;ch du lịch đều cảm nhận du thuyền được thiết kế tối t&acirc;n nhất n&ecirc;n ở trạng th&aacute;i lu&ocirc;n c&acirc;n bằng, kh&ocirc;ng kh&aacute;c g&igrave; đang ở tr&ecirc;n bờ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Những hoạt động giải tr&iacute; đa dạng, c&aacute;c m&ocirc;n thể thao, chương tr&igrave;nh biểu diễn game show s&ocirc;i động v&agrave; v&ocirc; số dịch vụ kh&aacute;c tr&ecirc;n du thuyền 5 sao đ&atilde; nhanh ch&oacute;ng đ&aacute;nh tan hồ nghi về sự nh&agrave;m ch&aacute;n như suy nghĩ ban đầu của nhiều du kh&aacute;ch khi chưa c&oacute; dịp đặt ch&acirc;n l&ecirc;n du thuyền, nơi được v&iacute; như &ldquo;kh&aacute;ch sạn nổi&rdquo; 5 sao.<br />\r\n<br />\r\nN&oacute;i về vấn đề n&agrave;y, b&agrave; Đo&agrave;n Thị Thanh Tr&agrave;, Gi&aacute;m đốc Tiếp thị-Truyền th&ocirc;ng, C&ocirc;ng ty Dịch vụ Lữ h&agrave;nh Saigontourist, cho biết c&ocirc;ng ty n&agrave;y đang hợp t&aacute;c với Royal Caribbean International phục vụ du kh&aacute;ch Việt Nam c&aacute;c tour du lịch cao cấp Premium Travel với c&aacute;c du thuyền sang trọng như Mariner of the Seas, Quantum of the Seas, Voyager of the Seas... nhằm mang lại những trải nghiệm mới lạ với dịch vụ đa dạng, gi&aacute; tour cạnh tranh, hải tr&igrave;nh phong ph&uacute;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Gi&aacute;m đốc Truyền th&ocirc;ng của C&ocirc;ng ty Saigontourist mi&ecirc;u tả du thuyền được thiết kế như những kh&aacute;ch sạn nổi với tiện nghi v&agrave; c&ocirc;ng nghệ tối t&acirc;n, tải trọng l&ecirc;n đến h&agrave;ng trăm ngh&igrave;n tấn, c&ugrave;ng sức chứa h&agrave;ng ngh&igrave;n kh&aacute;ch. Du thuyền đẳng cấp của c&aacute;c tập đo&agrave;n t&agrave;u biển quốc tế cao cấp đ&aacute;p ứng nhu cầu cho mọi đối tượng kh&aacute;ch h&agrave;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Ch&iacute;nh những tiện &iacute;ch n&agrave;y đ&atilde; tạo ra sự đ&oacute;n nhận nhiệt t&igrave;nh của du kh&aacute;ch đối với dịch vụ du lịch mới n&agrave;y. Điều đ&oacute; tạo động lực gi&uacute;p một số c&ocirc;ng ty du lịch trong thời gian vừa qua, đặc biệt l&agrave; những th&aacute;ng đầu năm mạnh dạn tung ra h&agrave;ng loạt g&oacute;i sản phẩm du thuyền hấp dẫn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">C&ocirc;ng ty Saigontourist đưa ra mức gi&aacute; kh&aacute; &ldquo;dễ thở&rdquo; cho dịch vụ từ l&acirc;u được coi l&agrave; d&agrave;nh cho giới thượng lưu n&agrave;y.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Dịp nghỉ lễ 30/4 v&agrave; 1/5/2016, du kh&aacute;ch c&oacute; cơ hội trải nghiệm những dịch vụ đẳng cấp tr&ecirc;n du thuyền 5 sao kh&aacute;m ph&aacute; h&ograve;n đảo xinh đẹp Penang ở xứ sở cọ Malaysia.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Đ&aacute;ng ch&uacute; &yacute;, Saigontourist đưa mức gi&aacute; chỉ từ 9,99 triệu đồng cho g&oacute;i sản phẩm n&agrave;y. Tour n&agrave;y sẽ khởi h&agrave;nh v&agrave;o ng&agrave;y 29, 30/4 từ cả Th&agrave;nh phố Hồ Ch&iacute; Minh v&agrave; H&agrave; Nội.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\"><strong>Khẳng định thế mạnh</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Chỉ t&iacute;nh ri&ecirc;ng trong những ng&agrave;y đầu th&aacute;ng Ba (từ ng&agrave;y 2-6/3), Saigontourist tiếp đ&oacute;n tổng cộng 8.350 du kh&aacute;ch v&agrave; thuyền vi&ecirc;n của 8 lượt chuyến t&agrave;u biển quốc tế cập tải c&aacute;c cảng đến Hạ Long (Quảng Ninh), Ti&ecirc;n Sa (Đ&agrave; Nẵng), Ch&acirc;n M&acirc;y (Huế), T&acirc;n Cảng C&aacute;i M&eacute;p (B&agrave; Rịa-Vũng T&agrave;u) tham quan c&aacute;c th&agrave;nh phố du lịch ch&iacute;nh ở cả ba miền đất nước theo c&aacute;c h&agrave;nh tr&igrave;nh du lịch do Saigontourist phục vụ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">C&aacute;c hải tr&igrave;nh li&ecirc;n tuyến xuy&ecirc;n Việt được xem l&agrave; thế mạnh của du lịch t&agrave;u biển đến Việt Nam với hệ thống cảng biển trải d&agrave;i, kết hợp với đặc trưng ri&ecirc;ng của từng v&ugrave;ng miền tại từng điểm cập cảng trải khắp Bắc-Trung-Nam.<br />\r\n<br />\r\nKh&ocirc;ng ri&ecirc;ng g&igrave; c&ocirc;ng ty du lịch khẳng định thế mạnh về du lịch t&agrave;u biển m&agrave; ngay cả những th&agrave;nh phố được mệnh danh l&agrave; th&agrave;nh phố biển như Đ&agrave; Nẵng cũng đ&atilde; bắt nhịp kh&aacute; nhanh với xu hướng n&agrave;y.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Theo th&ocirc;ng tin từ Sở Văn h&oacute;a, Thể thao, Du lịch Đ&agrave; Nẵng, thời gian qua, l&atilde;nh đạo th&agrave;nh phố tạo mọi điều kiện thuận lợi cho c&aacute;c h&atilde;ng t&agrave;u đến với th&agrave;nh phố, đồng thời chỉ đạo s&acirc;u s&aacute;t trong vấn đề n&acirc;ng cấp Cảng Đ&agrave; Nẵng trở th&agrave;nh cảng biển chuy&ecirc;n dụng cho du lịch.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Ng&agrave;nh du lịch Đ&agrave; Nẵng t&iacute;ch cực ph&aacute;t triển c&aacute;c tour tuyến du lịch mới, x&acirc;y dựng điểm đến v&agrave; n&acirc;ng chất lượng dịch vụ phục vụ kh&aacute;ch t&agrave;u biển. N&acirc;ng cao chất lượng phục vụ tại c&aacute;c điểm tham quan du lịch của th&agrave;nh phố như Bảo t&agrave;ng Ch&agrave;m, Ngũ H&agrave;nh Sơn, đ&egrave;o Hải V&acirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Địa phương k&ecirc;u gọi đầu tư c&aacute;c cơ sở hạ tầng, x&acirc;y dựng c&aacute;c điểm du lịch mới như khu suối nước n&oacute;ng Thần T&agrave;i, Khu vui chơi Asia Park,&hellip;Đội ngũ X&iacute;ch l&ocirc; du lịch, c&aacute;c l&aacute;i xe taxi, l&aacute;i xe du lịch ng&agrave;y c&agrave;ng chuy&ecirc;n nghiệp trong phong c&aacute;ch phục vụ kh&aacute;ch.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, năm 2016, Đ&agrave; Nẵng tạo mọi điều kiện cho c&aacute;c h&atilde;ng t&agrave;u du lịch quốc tế đưa kh&aacute;ch du lịch đến th&agrave;nh phố Đ&agrave; Nẵng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">C&aacute;c ng&agrave;nh, c&aacute;c lực lượng chức năng tăng cường kiểm tra, đảm bảo c&ocirc;ng t&aacute;c an ninh, an to&agrave;n, chống đeo b&aacute;m ch&egrave;o k&eacute;o kh&aacute;ch du lịch tr&ecirc;n địa b&agrave;n th&agrave;nh phố.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Địa phương chủ trương cho h&atilde;ng t&agrave;u Star Cruise được triển khai c&aacute;c hoạt động vui chơi giải tr&iacute; tr&ecirc;n t&agrave;u trong thời gian t&agrave;u qua đ&ecirc;m tại Đ&agrave; Nẵng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Sản phẩm du lịch được đa dạng h&oacute;a, đặc biệt l&agrave; c&aacute;c sản phẩm du lịch mới như du lịch đường s&ocirc;ng, chương tr&igrave;nh giải tr&iacute; về đ&ecirc;m để thu h&uacute;t kh&aacute;ch du lịch t&agrave;u biển lưu tr&uacute; qua đ&ecirc;m.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Dự kiến trong năm 2016, Đ&agrave; Nẵng sẽ đ&oacute;n được 84 chuyến t&agrave;u biển với 83.630 lượt kh&aacute;ch tăng 196,7% so với năm 2015 (năm 2015: 43 chuyến với 28.190 lượt kh&aacute;ch).</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Trong qu&yacute; 1/2016 c&oacute; 39 chuyến t&agrave;u biển cập cảng Đ&agrave; Nẵng với 39.700 lượt kh&aacute;ch, tăng 167,59% so với c&ugrave;ng kỳ năm 2015 (qu&yacute; 1/2015 đ&oacute;n được 21 chuyến với 14.836 lượt kh&aacute;ch)./.</span></span></span></p>\r\n',1,'2016-04-08 21:52:34','2016-04-08 21:52:34',NULL,1,0),(2,'Đà Nẵng sẽ tổ chức lễ hội Hanami đầu tiên tại Asia Park',NULL,'<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 12px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; color: rgb(27, 27, 27); font-family: \'Open Sans\', sans-serif; line-height: 18.4px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\"><strong>Trong 2 ng&agrave;y 9 v&agrave; 10/4, Đ&agrave; Nẵng sẽ tổ chức lễ hội Hanami (Nhật Bản) lần đầu ti&ecirc;n tại Asia Park (1 Phan Đăng Lưu, quận Hải Ch&acirc;u).</strong></span></span></span></div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 12px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; color: rgb(27, 27, 27); font-family: \'Open Sans\', sans-serif; line-height: 18.4px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">&nbsp;</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 12px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; color: rgb(27, 27, 27); font-family: \'Open Sans\', sans-serif; line-height: 18.4px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 12px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; vertical-align: baseline; background: transparent;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 12px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; vertical-align: baseline; background: transparent;\">\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"Đà Nẵng sẽ tổ chức lễ hội Hanami đầu tiên tại Asia Park\" src=\"http://www.dulich.vn/FileManager/vietpro2203/00asiapark1-Copy-1.jpg\" style=\"height:307px; margin:0px; width:550px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Lễ hội Hanami tại Asia Park sẽ t&aacute;i hiện ch&acirc;n thực lễ hội Hanami truyền thống được tổ chức v&agrave;o th&aacute;ng 4 h&agrave;ng năm tại Nhật Bản. Đến với lễ hội, du kh&aacute;ch sẽ c&oacute; cơ hội chi&ecirc;m ngưỡng vẻ đẹp rạng rỡ, thuần khiết của hoa anh đ&agrave;o, lo&agrave;i hoa đặc trưng của xứ sở Mặt trời mọc. Du kh&aacute;ch cũng được thưởng thức nhiều chương tr&igrave;nh nghệ thuật đặc sắc của xứ sở Ph&ugrave; tang do đ&iacute;ch th&acirc;n c&aacute;c nghệ sĩ Nhật Bản tr&igrave;nh diễn như Biểu diễn Tr&agrave; đạo, Thư ph&aacute;p đại tự, điệu m&uacute;a Yosakoi truyền thống&hellip;</span></span></span></p>\r\n\r\n<p><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">C&aacute;c bạn trẻ sẽ rất h&agrave;o hứng với chương tr&igrave;nh biểu diễn thời trang theo phong c&aacute;ch Cosplay- phong c&aacute;ch thời trang đường phố truyền thống Nhật Bản hay tham quan c&aacute;c gian h&agrave;ng giới thiệu văn h&oacute;a ẩm thực, trang phục Kimono truyền thống&hellip;</span></span></span></p>\r\n\r\n<p><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Đến với Asia Park những ng&agrave;y n&agrave;y, du kh&aacute;ch sẽ được chi&ecirc;m ngưỡng Khu Nhật Bản được trang tr&iacute; rực rỡ hoa anh đ&agrave;o. C&ugrave;ng với hồ rồng biểu trưng cho tinh thần v&otilde; sĩ đạo, nh&agrave; h&agrave;ng caf&eacute; Momo được thiết kế theo kiến tr&uacute;c shinden-zukuri d&agrave;nh cho giới qu&yacute; tộc Nhật Bản, khu trưng b&agrave;y lưu niệm với hơn 3.000 sản phẩm thủ c&ocirc;ng mỹ nghệ của người Nhật...du kh&aacute;ch sẽ v&ocirc; c&ugrave;ng ấn tượng với Lễ hội Hanami lần đầu ti&ecirc;n được tổ chức tại th&agrave;nh phố s&ocirc;ng H&agrave;n.</span></span></span></p>\r\n\r\n<p><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Hanami Festival sẽ mang đến trải nghiệm chưa từng c&oacute; về một &ldquo;Nhật Bản giữa l&ograve;ng Asia Park&rdquo;. Dự kiến đ&acirc;y sẽ l&agrave; hoạt động thường ni&ecirc;n tại Asia Park nhằm chia sẻ những gi&aacute; trị văn h&oacute;a, thẩm mỹ của nền văn h&oacute;a Nhật với người Đ&agrave; Nẵng v&agrave; du kh&aacute;ch, gi&uacute;p gia tăng sự hiểu biết lẫn nhau v&agrave; t&igrave;nh hữu nghị giữa người d&acirc;n hai nước. Sự kiện c&agrave;ng c&oacute; &yacute; nghĩa s&acirc;u sắc hơn khi được tổ chức tại Asia Park- nơi hội tụ nền văn h&oacute;a ti&ecirc;u biểu của 10 quốc gia tại Ch&acirc;u &Aacute;, trong đ&oacute; c&oacute; Nhật Bản.</span></span></span></p>\r\n\r\n<p><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Lễ hội Hanami l&agrave; một trong nhiều hoạt động được Asia Park triển khai trong nỗ lực quảng b&aacute; s&acirc;u rộng c&aacute;c gi&aacute; trị văn h&oacute;a truyền thống c&aacute;c nước Ch&acirc;u &Aacute;, &nbsp;nhằm mang tới một sản phẩm du lịch độc đ&aacute;o cho người d&acirc;n th&agrave;nh phố s&ocirc;ng H&agrave;n v&agrave; du kh&aacute;ch. C&ocirc;ng vi&ecirc;n Asia Park do Tập đo&agrave;n Sun Group đầu tư l&agrave; c&ocirc;ng vi&ecirc;n vui chơi giải tr&iacute; h&agrave;ng đầu Đ&ocirc;ng Nam &Aacute;&nbsp;với quy m&ocirc; đầu tư&nbsp; giai đoạn 1 l&ecirc;n đến 4.000 tỉ đồng.&nbsp;</span></span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n',1,'2016-04-08 21:55:40','2016-04-08 21:55:40',NULL,1,0),(3,'Những xu hướng du lịch \"lên ngôi\" năm 2016',NULL,'<p style=\"text-align:justify\"><strong><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Du lịch tr&ecirc;n s&ocirc;ng, c&aacute;c điểm đến v&ugrave;ng Bắc &Acirc;u, du lịch mạo hiểm... l&agrave; những xu hướng nổi trội của ng&agrave;nh du lịch thế giới năm 2016, theo&nbsp;nhận định của nhiều tổ chức nghi&ecirc;n cứu du lịch quốc tế.</span></span></span></strong><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"Những xu hướng du lịch &quot;lên ngôi&quot; năm 2016\" src=\"http://www.dulich.vn/FileManager/vietpro2203/voyage6a-1451964146-Copy-1.jpg\" style=\"height:405px; margin:0px; width:600px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n<br />\r\n<br />\r\n<strong><em>Thụy&nbsp;Điển được xem l&agrave; nơi l&yacute; tưởng cho du kh&aacute;ch đơn th&acirc;n - Ảnh: fotolia</em></strong>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Trong số xu hướng du lịch sẽ l&ecirc;n ng&ocirc;i trong năm 2016, c&aacute;c nh&agrave; tổ chức du lịch đặc biệt quan t&acirc;m đến&nbsp;loại h&igrave;nh h&agrave;nh&nbsp;tr&igrave;nh du&nbsp;lịch theo chủ&nbsp;đề, du lịch đơn độc (nhất l&agrave; ph&aacute;i nữ) v&agrave; du thuyền tr&ecirc;n s&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Đối với c&aacute;c điểm đến trong năm, khu vực Đ&ocirc;ng &Acirc;u&nbsp;c&ugrave;ng&nbsp;c&aacute;c nước&nbsp;Bắc&nbsp;&Acirc;u&nbsp;đang ng&agrave;y c&agrave;ng h&uacute;t kh&aacute;ch. Những&nbsp;điểm&nbsp;đến v&ugrave;ng cực chưa&nbsp;được phổ biến trước&nbsp;đ&acirc;y như Greenland, Bắc Cực v&agrave; Nam Cực sẽ g&oacute;p phần chia sẻ thị phần du lịch...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\"><strong>*&nbsp;L&agrave;n s&oacute;ng du lịch&nbsp;đến khu vực Bắc&nbsp;&Acirc;u</strong>&nbsp;được dự b&aacute;o diễn ra trong năm 2016 khi nhiều h&atilde;ng h&agrave;ng kh&ocirc;ng trong khu vực đưa ra&nbsp;những mức gi&aacute; cực kỳ hấp dẫn ngay từ đầu năm.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">C&aacute;c h&atilde;ng du&nbsp;thuyền lớn cũng&nbsp;đ&atilde;&nbsp;giới thiệu nhiều h&agrave;nh tr&igrave;nh&nbsp;đến Bắc Cực như Alaska, Greenland, Spitsbergen (Na Uy) v&agrave; v&ugrave;ng&nbsp;Viễn Đ&ocirc;ng của Nga.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Sức hấp dẫn của Bắc &Acirc;u sẽ tỏa s&aacute;ng năm nay&nbsp;khi&nbsp;Thụy&nbsp;Điển được xem l&agrave; nơi l&yacute; tưởng cho du kh&aacute;ch đơn th&acirc;n trong c&aacute;c loại h&igrave;nh kh&aacute;m ph&aacute; bằng xe đạp, thuyền, đi bộ...&nbsp;hay Iceland, nơi c&oacute; nhiều bối cảnh xuất hiện trong phim ảnh thế giới như d&ograve;ng s&ocirc;ng băng Vatnaj&ouml;kull, động Grjotagja...</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"Những xu hướng du lịch &quot;lên ngôi&quot; năm 2016\" src=\"http://www.dulich.vn/FileManager/vietpro2203/voyage6-1451964130.jpg\" style=\"height:337px; margin:0px; width:600px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n<br />\r\n<br />\r\n<em><strong>Năm 2016 sẽ c&oacute; nhiều người đến với phong cảnh si&ecirc;u thực ở Iceland - Ảnh: fotolia</strong></em>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\"><strong>*&nbsp;&quot;Du lịch&nbsp;ng&agrave;y c&agrave;ng hợp t&aacute;c&quot;</strong>&nbsp;trở th&agrave;nh xu hướng mới&nbsp;khi 60% người từ 22 -&nbsp;42 tuổi&nbsp;th&iacute;ch sử dụng dịch vụ chia sẻ&nbsp;với người c&ugrave;ng mục&nbsp;đ&iacute;ch trong h&agrave;nh tr&igrave;nh du lịch v&agrave; 78% du kh&aacute;ch cho rằng c&aacute;c chuyến du lịch sẽ th&uacute; vị&nbsp;hơn khi c&oacute; một&nbsp;người bạn&nbsp;hướng dẫn&nbsp;tại chỗ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">V&agrave; theo b&agrave; Karine Miron, phụ tr&aacute;ch Parkour3 (c&ocirc;ng ty tr&ecirc;n mạng chuy&ecirc;n về chiến lược nghi&ecirc;n cứu thống k&ecirc; xu hướng du lịch), trong năm 2016&nbsp;xu hướng n&agrave;y trở n&ecirc;n&nbsp;đơn giản hơn dựa&nbsp;tr&ecirc;n việc&nbsp;chia sẻ kinh nghiệm, trao&nbsp;đổi nh&agrave;, xe, thậm ch&iacute;&nbsp;li&ecirc;n kết giữa d&acirc;n&nbsp;địa phương v&agrave; du kh&aacute;ch. &nbsp;&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Tổ chức PwC của Ph&aacute;p cho biết c&aacute;c c&ocirc;ng ty du lịch&nbsp;chọn m&ocirc; h&igrave;nh hợp t&aacute;c&nbsp;như tr&ecirc;n c&oacute; tốc&nbsp;độ tăng trưởng hằng năm khoảng 30% v&agrave; tiếp diễn trong 10 năm tới mặc d&ugrave; những ti&ecirc;u chuẩn ng&agrave;y c&agrave;ng khắt&nbsp;khe hơn.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">L&yacute; do,&nbsp;với xu hướng n&agrave;y, du kh&aacute;ch&nbsp;thật&nbsp;sự cảm nhận&nbsp;được sự gần gũi với&nbsp;c&aacute;c gi&aacute; trị kinh tế v&agrave;&nbsp;văn h&oacute;a mang lại cho họ&nbsp;như hiểu r&otilde; phong tục tập qu&aacute;n&nbsp;của d&acirc;n địa&nbsp;phương, hưởng chi ph&iacute; thấp trong c&aacute;c dịch vụ,&nbsp;được tiếp cận c&aacute; nh&acirc;n nhiều hơn...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">B&agrave; Dianne Dredge, gi&aacute;o sư Đại học Aalborg ở Copenhagen, cho rằng cuộc khủng hoảng kinh tế khiến người d&acirc;n c&aacute;c nước Nam &Acirc;u như T&acirc;y Ban Nha, Bồ Đ&agrave;o Nha, &Yacute;, Hi Lạp&nbsp;nhanh ch&oacute;ng chấp nhận kh&aacute;i niệm hợp&nbsp;t&aacute;c n&agrave;y.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Theo một nghi&ecirc;n cứu của tổ chức Nielsen, 55%&nbsp;người T&acirc;y Ban Nha cho biết họ&nbsp;rất quan t&acirc;m đến xu hướng n&agrave;y trong bối cảnh du kh&aacute;ch ng&agrave;y c&agrave;ng&nbsp;chuy&ecirc;n nghiệp v&agrave; độ tuổi du lịch ng&agrave;y c&agrave;ng tăng.</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"Những xu hướng du lịch &quot;lên ngôi&quot; năm 2016\" src=\"http://www.dulich.vn/FileManager/vietpro2203/voyage4a-1451964506.jpg\" style=\"height:449px; margin:0px; width:600px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n<br />\r\n<br />\r\n<em><strong>Xu hướng &quot;du lịch hợp t&aacute;c&quot; sẽ ph&aacute;t triển mạnh - Ảnh:&nbsp;Maksym Yemelyanov</strong></em>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\"><strong>*&nbsp;Kh&aacute;m ph&aacute; những th&agrave;nh phố kh&ocirc;ng nằm trong&nbsp;c&aacute;c cung đường quen thuộc</strong>&nbsp;đang trở n&ecirc;n phổ biến&nbsp;với những đ&ocirc; thị chưa được đ&aacute;m đ&ocirc;ng biết tới v&agrave; ho&agrave;n to&agrave;n đứng ngo&agrave;i lĩnh vực du lịch đại tr&agrave; sẽ l&ecirc;n ng&ocirc;i trong m&ugrave;a du lịch tới.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Những c&aacute;i t&ecirc;n lạ như Kotor ở Montenegro, Quito ở Ecuador, George Town&nbsp;ở Malaysia hay&nbsp;Rotterdam ở H&agrave; Lan, Fremantle ở &Uacute;c v&igrave; thế&nbsp;đ&atilde; trở th&agrave;nh những điểm đến h&agrave;ng đầu trong năm 2016&nbsp;theo b&igrave;nh chọn của&nbsp;Lonely Planet.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Theo xu hướng n&agrave;y, kh&aacute;ch du lịch&nbsp;muốn thỏa m&atilde;n l&ograve;ng khao kh&aacute;t kh&aacute;m ph&aacute; những con hẻm nhỏ y&ecirc;n b&igrave;nh, thưởng thức ẩm thực địa phương trong những nh&agrave; h&agrave;ng kh&ocirc;ng qu&aacute; bận rộn với đ&aacute;m đ&ocirc;ng du&nbsp;kh&aacute;ch...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Một&nbsp;lợi thế kh&aacute;c l&agrave; kh&aacute;ch du lịch&nbsp;c&oacute; thể&nbsp;tiết kiệm đ&aacute;ng kể chi ph&iacute; du lịch khi&nbsp;c&aacute;c dịch vụ hạ tầng du&nbsp;lịch tại c&aacute;c th&agrave;nh phố tr&ecirc;n&nbsp;c&ograve;n duy tr&igrave;&nbsp;ở mức thấp&nbsp;đ&aacute;ng kể so với những&nbsp;điểm&nbsp;đến quen thuộc. &nbsp;</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"Những xu hướng du lịch &quot;lên ngôi&quot; năm 2016\" src=\"http://www.dulich.vn/FileManager/vietpro2203/voyage4-1451964315.jpg\" style=\"height:337px; margin:0px; width:600px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n<br />\r\n<br />\r\n<em><strong>N&uacute;i lửa Cotopaxi, Quito, Ecuador - Ảnh: fotolia</strong></em>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\"><strong>*&nbsp;Thỏa m&aacute;u phi&ecirc;u lưu ở những v&ugrave;ng đất lạ sẽ l&agrave; một tr&agrave;o lưu ở du kh&aacute;ch trẻ.</strong>&nbsp;Theo b&agrave; Lara Lazare, ph&oacute; chủ tịch C&ocirc;ng ty quản l&yacute; du lịch Vision Travel của Canada, c&aacute;c h&agrave;nh tr&igrave;nh du lịch thể thao v&agrave; mạo hiểm sẽ l&agrave; một trong những lựa chọn h&agrave;ng đầu của du kh&aacute;ch nước n&agrave;y bởi&nbsp;&quot;phơi nắng v&agrave; thư gi&atilde;n b&ecirc;n hồ bơi kh&ocirc;ng c&ograve;n hấp dẫn nữa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Người Canada muốn đến với những h&agrave;nh tr&igrave;nh năng động hơn v&agrave; t&igrave;m kiếm những điểm đến đ&aacute;p ứng c&aacute;c hoạt động thể thao nhiều hơn&quot;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Những hoạt động phổ biến nhất cho kỳ nghỉ của du kh&aacute;ch phương T&acirc;y ở c&aacute;c v&ugrave;ng đất lạ từ loại h&igrave;nh cưỡi ngựa dọc theo bờ biển v&ugrave;ng Carib&ecirc;, d&atilde; ngoại bằng xe đạp mọi địa h&igrave;nh trong một c&aacute;nh rừng n&agrave;o đ&oacute; hay bơi c&ugrave;ng c&aacute; mập...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">V&agrave; theo thống k&ecirc; gần đ&acirc;y, đa số du kh&aacute;ch Canada nghĩ đến Việt Nam v&agrave; Campuchia&nbsp;khi muốn&nbsp;đến ch&acirc;u&nbsp;&Aacute; hay Peru hoặc Costa Rica&nbsp;ở ch&acirc;u Mỹ...&nbsp;</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"Những xu hướng du lịch &quot;lên ngôi&quot; năm 2016\" src=\"http://www.dulich.vn/FileManager/vietpro2203/voyage3-1451964620.jpg\" style=\"height:337px; margin:0px; width:600px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n<br />\r\n<br />\r\n<em><strong>Bơi c&ugrave;ng c&aacute; mập, cuộc phi&ecirc;u lưu mới trong xu hướng du lịch 2016 - Ảnh: fotolia</strong></em>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\"><strong>*&nbsp;Những thanh ni&ecirc;n năng động, những phụ nữ đơn th&acirc;n sẽ du lịch thường xuy&ecirc;n hơn.</strong>&nbsp;Theo&nbsp;nhiều&nbsp;tổ chức nghi&ecirc;n cứu,&nbsp;ph&acirc;n kh&uacute;c du lịch&nbsp;đơn lẻ sẽ c&ograve;n tăng trưởng mạnh mẽ&nbsp;khi&nbsp;xu hướng&nbsp;độc th&acirc;n, h&ocirc;n nh&acirc;n muộn m&agrave;ng, ly&nbsp;dị, g&oacute;a bụa...&nbsp;đang ng&agrave;y c&agrave;ng tăng tại nhiều nước tr&ecirc;n thế giới.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Việc ho&agrave;n to&agrave;n tự do trong lựa chọn h&agrave;nh tr&igrave;nh, được tự do kh&aacute;m kh&aacute; những nền văn h&oacute;a kh&aacute;c nhau theo một thời gian t&ugrave;y th&iacute;ch,&nbsp;được&nbsp;gặp gỡ nhiều bạn mới... đ&atilde; gi&uacute;p những người du lịch đơn th&acirc;n kh&ocirc;ng phải buồn ch&aacute;n. V&igrave; vậy, đối tượng du kh&aacute;ch n&agrave;y đ&atilde; tăng 64% trong v&agrave;i năm gần&nbsp;đ&acirc;y&nbsp;trong lĩnh vực&nbsp;đặt chỗ&nbsp;du lịch&nbsp;đơn lẻ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Để&nbsp;hỗ trợ&nbsp;người du lịch đơn lẻ được an to&agrave;n v&agrave; tr&aacute;nh những sự cố, tạp ch&iacute; du lịch&nbsp;Traver and Leisure&nbsp;c&oacute; trụ sở ở Mỹ với hơn 4,8 triệu độc giả&nbsp;đ&atilde; b&igrave;nh chọn 20 nước c&oacute; chỉ số an to&agrave;n.&nbsp;Theo đ&oacute;, Việt Nam, Nhật, New Zealand, Na Uy, Thụy Sĩ, &Aacute;o, Thụy Điển... l&agrave; những nước h&agrave;ng đầu trong lĩnh vực n&agrave;y.</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"Những xu hướng du lịch &quot;lên ngôi&quot; năm 2016\" src=\"http://www.dulich.vn/FileManager/vietpro2203/voyage2b-1451964693.jpg\" style=\"height:402px; margin:0px; width:600px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n<br />\r\n<br />\r\n<em><strong>Một m&igrave;nh du lịch - Ảnh: wp</strong></em>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">* Đa số c&aacute;c chuy&ecirc;n gia du lịch thế giới khẳng định 2016 sẽ chứng kiến sự b&ugrave;ng nổ của&nbsp;<strong>du&nbsp;lịch tr&ecirc;n s&ocirc;ng</strong>. Trong đ&oacute;, những cung đường du lịch tr&ecirc;n s&ocirc;ng&nbsp;h&uacute;t kh&aacute;ch sẽ diễn ra dọc s&ocirc;ng Rhin, s&ocirc;ng Danube, s&ocirc;ng Sein ở ch&acirc;u &Acirc;u.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Tại ch&acirc;u &Aacute;, du thuyền sẽ tấp nập tr&ecirc;n c&aacute;c con s&ocirc;ng Irrawaddy ở Myanmar, Dương Tử (Trung Quốc),&nbsp;Mekong ở Campuchia v&agrave; Cửu Long ở&nbsp;Việt Nam. Tại ch&acirc;u Mỹ, d&ograve;ng s&ocirc;ng St. Lawrence v&agrave; Mississippi lu&ocirc;n l&agrave; giấc mơ kh&aacute;m ph&aacute; của du kh&aacute;ch...</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, du thuyền tr&ecirc;n biển tiếp tục tăng trưởng sau khi đạt hơn 22 triệu kh&aacute;ch trong năm trước.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Hiện tại, lĩnh vực du thuyền tr&ecirc;n biển đ&atilde; c&oacute; những bước đột ph&aacute; khi đưa ra nhiều loại h&igrave;nh du thuyền chuy&ecirc;n&nbsp;đề, từ du thuyền yoga, du thuyền c&ugrave;ng c&aacute;c ng&ocirc;i sao thể thao hay ca nhạc đến du thuyền h&igrave;nh xăm c&oacute; sự tham dự của c&aacute;c ng&ocirc;i sao trong lĩnh vực n&agrave;y c&ugrave;ng việc tổ chức cuộc&nbsp;thi h&igrave;nh xăm.</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"Những xu hướng du lịch &quot;lên ngôi&quot; năm 2016\" src=\"http://www.dulich.vn/FileManager/vietpro2203/voyage1a-1451964782.jpg\" style=\"height:199px; margin:0px; width:600px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n<br />\r\n<br />\r\n<em><strong>Du lịch tr&ecirc;n s&ocirc;ng ở ch&acirc;u &Acirc;u - Ảnh: croisieurope</strong></em>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"Những xu hướng du lịch &quot;lên ngôi&quot; năm 2016\" src=\"http://www.dulich.vn/FileManager/vietpro2203/voyage1c-1451964941.jpg\" style=\"height:399px; margin:0px; width:600px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n<br />\r\n<br />\r\n<em><strong>Du thuyền yoga ở Thổ Nhĩ Kỳ - Ảnh: lapresse</strong></em>\r\n\r\n<p>&nbsp;</p>\r\n',1,'2016-04-08 21:58:17','2016-04-08 21:58:17',NULL,1,0),(4,'8 trải nghiệm hấp dẫn không thể bỏ qua khi tới Tây Nguyên',NULL,'<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\"><strong>Nếu c&oacute; dịp đặt ch&acirc;n đến T&acirc;y Nguy&ecirc;n, du kh&aacute;ch đừng qu&ecirc;n chinh phục ng&atilde; ba Đ&ocirc;ng Dương, ngắm sao đ&ecirc;m ở Măng Đen hay xem biểu diễn cồng chi&ecirc;ng.</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Dưới đ&acirc;y l&agrave; 8 điều l&agrave;m cho chuyến đi T&acirc;y Nguy&ecirc;n của bạn th&ecirc;m phần th&uacute; vị v&agrave; đ&aacute;ng nhớ.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Gh&eacute; thăm &quot;đ&ocirc;i mắt Pleiku&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Biển Hồ (hồ T&rsquo;Nưng) l&agrave; một trong những hồ nước tự nhi&ecirc;n thơ mộng nhất T&acirc;y Nguy&ecirc;n. Hồ nước n&agrave;y được h&igrave;nh th&agrave;nh từ một miệng n&uacute;i lửa v&agrave; rất rộng. Người ta v&iacute; Biển Hồ như đ&ocirc;i mắt của người d&acirc;n Gia Lai v&agrave; người d&acirc;n th&agrave;nh phố Pleiku.</span></span></p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(27, 27, 27); font-family:arial; font-size:14px; line-height:normal; margin:0px auto 10px; max-width:100%; padding:0px; text-align:justify; vertical-align:baseline; width:470px; zoom:1\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"Vẻ đẹp của Biển Hồ. Ảnh: Thuonghieuvn\" src=\"http://www.dulich.vn/FileManager/vietpro2203/trai-nghiem-tay-nguyen-ivivu-c-2083-7506-1449111776-Copy-1.jpg\" style=\"margin:0px; width:470px\" /></span></span>\r\n\r\n			<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n			</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<p style=\"text-align:center\"><em><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Vẻ đẹp của Biển Hồ. Ảnh:&nbsp;<em>Thuonghieuvn</em></span></span></span></em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Đứng tr&ecirc;n bờ nh&igrave;n ra xa, bạn sẽ thấy một m&agrave;u xanh bạt ng&agrave;n của nước biển. Hồ nằm tr&ecirc;n n&uacute;i, n&ecirc;n khi du kh&aacute;ch đứng tr&ecirc;n bờ, gi&oacute; từ hồ nước với gi&oacute; rừng sẽ tạo cảm gi&aacute;c m&aacute;t lạnh. Đạp xe băng qua những c&aacute;nh rừng l&agrave; c&aacute;ch bạn n&ecirc;n l&agrave;m để kh&aacute;m ph&aacute; vẻ đẹp b&iacute; ẩn trong &quot;đ&ocirc;i mắt Pleiku&quot; ấy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Xem một buổi biểu diễn cồng chi&ecirc;ng</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Kh&ocirc;ng gian văn h&oacute;a cồng chi&ecirc;ng l&agrave; di sản văn h&oacute;a phi vật thể phong ph&uacute; của c&aacute;c d&acirc;n tộc ở T&acirc;y Nguy&ecirc;n. Cồng chi&ecirc;ng l&agrave; loại nhạc kh&iacute; bằng hợp kim đồng, c&oacute; khi pha v&agrave;ng, bạc hoặc đồng đen. Tất cả lễ hội trong năm, từ lễ thổi tai cho trẻ sơ sinh đến lễ bỏ mả, lễ c&uacute;ng m&aacute;ng nước, lễ mừng cơm mới, lễ đ&oacute;ng cửa kho, lễ đ&acirc;m tr&acirc;u&hellip; đều phải c&oacute; tiếng cồng chi&ecirc;ng như thứ để nối kết mọi người trong c&ugrave;ng một cộng đồng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Thưởng thức c&agrave; ph&ecirc; tại &quot;thủ phủ c&agrave; ph&ecirc;&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Th&agrave;nh phố Bu&ocirc;n Ma Thuột từ l&acirc;u được xemnhư l&agrave; &ldquo;thủ phủ c&agrave; ph&ecirc;&rdquo; của Việt Nam. Đến đ&acirc;y bạn kh&ocirc;ng chỉ nh&igrave;n ngắm những đồn điền c&agrave; ph&ecirc; xanh ng&uacute;t tầm mắt, m&agrave; c&ograve;n được thưởng thức ly c&agrave; ph&ecirc; thơm ngon nhất. Ngồi uống một ly trong l&agrave;ng c&agrave; ph&ecirc; Trung Nguy&ecirc;n, v&agrave;o một buổi chiều mưa, đ&oacute; c&oacute; thể l&agrave; ly c&agrave; ph&ecirc; đ&aacute;ng nhớ nhất m&agrave; bạn từng thưởng thức.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Ngắm sao đ&ecirc;m ở Măng Đen</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Măng Đen nằm ở độ cao khoảng 1.200 m so với mặt nước biển, thuộc huyện Kon Plong, tỉnh Kon Tum. Đ&acirc;y được coi như dải ph&acirc;n c&aacute;ch ch&iacute;nh giữa Trường Sơn Đ&ocirc;ng v&agrave; Trường Sơn T&acirc;y. Kh&iacute; hậu nơi đ&acirc;y m&aacute;t mẻ quanh năm, nhiệt độ thường dưới 22 độ C. Rừng nguy&ecirc;n sinh bao bọc xung quanh v&agrave; rừng th&ocirc;ng cổ thụ rộng lớn nằm dọc theo quốc lộ 24. Ngo&agrave;i ra Măng Đen c&ograve;n c&oacute; nhiều hồ th&aacute;c, suối v&agrave; cảnh quan đẹp.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Đặc biệt, Măng Đen được mệnh danh l&agrave; một trong những địa điểm đẹp nhất để ngắm sao ở Việt Nam. Nếu bạn đến đ&acirc;y v&agrave;o l&uacute;c trời kh&ocirc; v&agrave; quang m&acirc;y, h&atilde;y chọn cho m&igrave;nh một b&atilde;i cỏ hay ban c&ocirc;ng tho&aacute;ng đ&atilde;ng để ch&igrave;m v&agrave;o khoảnh khắc l&atilde;ng mạn của sao trời.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Chinh phục ng&atilde; ba Đ&ocirc;ng Dương</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Cửa khẩu Bờ Y, nơi một con g&agrave; cất tiếng g&aacute;y cả ba nước c&ugrave;ng nghe l&agrave; địa chỉ kh&ocirc;ng thể bỏ qua nếu bạn c&oacute; dịp đến T&acirc;y Nguy&ecirc;n. L&agrave; ng&atilde; ba bi&ecirc;n giới giữa Việt Nam &ndash; L&agrave;o &ndash; Campuchia, đ&acirc;y l&agrave; cột mốc l&atilde;nh thổ quan trọng của quốc gia.</span></span></p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(27, 27, 27); font-family:arial; font-size:14px; line-height:normal; margin:0px auto 10px; max-width:100%; padding:0px; text-align:justify; vertical-align:baseline; width:470px; zoom:1\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"fdsf\" src=\"http://www.dulich.vn/FileManager/vietpro2203/trai-nghiem-tay-nguyen-ivivu-c-7555-5027-1449111776.jpg\" style=\"margin:0px; width:470px\" /></span></span>\r\n\r\n			<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n			</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<p><em><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Kh&ocirc;ng &iacute;t những bạn trẻ đ&ecirc;n T&acirc;y Nguy&ecirc;n để thỏa ch&iacute; chinh phục được ng&atilde; ba Đ&ocirc;ng Dương. Ảnh:&nbsp;<em>Nguyễn Văn Tuấn.</em></span></span></span></em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">C&oacute; nhiều đường v&agrave; phương tiện để đến với x&atilde; Bờ Y, huyện Ngọc Hồi, tỉnh Kon Tum. Nếu từ ph&iacute;a bắc, bạn c&oacute; thể đ&aacute;p m&aacute;y bay đến Bu&ocirc;n M&ecirc; Thuột (Đắk Lắk) v&agrave; tiếp tục t&igrave;m xe kh&aacute;ch để đến Ngọc Hồi. Ngo&agrave;i ra, xe kh&aacute;ch chạy từ TP HCM, hay c&aacute;c tỉnh ở dọc quốc lộ 1A như Quảng B&igrave;nh, Quảng Ng&atilde;i&hellip; đến Kon Tum rất nhiều.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Sau khi tham quan cửa khẩu quốc tế Bờ Y, theo con đường hơn 10 km v&ograve;ng v&egrave;o quanh mấy ngọn đồi, từ đ&acirc;y leo những bậc thang để chạm đến cột mốc l&agrave;m bằng đ&aacute; hoa cương l&agrave; bạn đ&atilde; đặt ch&acirc;n tới ng&atilde; ba Đ&ocirc;ng Dương. Cột mốc nằm tr&ecirc;n đỉnh n&uacute;i c&oacute; độ cao hơn 1.000 m so với mực nước biển, ghi danh ba quốc gia.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Cưỡi voi ở Vườn quốc gia Yok Đ&ocirc;n</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">L&agrave; điểm du lịch sinh th&aacute;i hấp dẫn với nhiều du kh&aacute;ch trong v&agrave; ngo&agrave;i nước, vườn quốc gia nằm tr&ecirc;n địa b&agrave;n x&atilde; Kr&ocirc;ng Na, huyện Bu&ocirc;n Đ&ocirc;n, c&aacute;ch Bu&ocirc;n Ma Thuột 40 km về ph&iacute;a t&acirc;y bắc.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Du kh&aacute;ch đến đ&acirc;y sẽ tận hưởng c&aacute;c loại h&igrave;nh du lịch sinh th&aacute;i đặc trưng như cưỡi voi, đi bộ, xe đạp địa h&igrave;nh trong c&aacute;c c&aacute;nh rừng nguy&ecirc;n sinh. Đặc biệt, ban đ&ecirc;m, bạn c&oacute; thể đi xem c&aacute;c lo&agrave;i th&uacute; hoang d&atilde;, ban ng&agrave;y đi du thuyền độc mộc tr&ecirc;n d&ograve;ng S&ecirc;r&ecirc;pok thơ mộng. Ngo&agrave;i ra, tham quan bu&ocirc;n l&agrave;ng, thưởng thức rượu cần với nhiều m&oacute;n đặc sản hấp dẫn đặc trưng của đồng b&agrave;o T&acirc;y Nguy&ecirc;n cũng l&agrave; &yacute; tưởng hay.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Tham quan th&aacute;c nước h&ugrave;ng vĩ Dray S&aacute;p</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Th&aacute;c Dray S&aacute;p hay th&aacute;c Chồng, l&agrave; th&aacute;c nước c&oacute; bề mặt trải d&agrave;i hơn 500 m, cao gần 20 m chia l&agrave;m 3 phần: thượng, trung v&agrave; hạ. Mỗi năm v&agrave;o đầu m&ugrave;a mưa, d&ograve;ng nước của con th&aacute;c đổ ầm ầm, tung bọt nước trắng x&oacute;a, v&igrave; vậy người &Ecirc;-đ&ecirc; hay gọi l&agrave; th&aacute;c kh&oacute;i. Ngọn th&aacute;c c&oacute; địa h&igrave;nh nằm trong một khu vực nhiều hang động, c&acirc;y rừng, d&acirc;y leo chằng chịt, bốn bề vang động &acirc;m thanh, cộng th&ecirc;m đường l&ecirc;n th&aacute;c quanh co, gập ghềnh với nhiều tảng đ&aacute; đầy r&ecirc;u phong. Những điều đ&oacute; khiến nơi đ&acirc;y trở th&agrave;nh một danh thắng nổi tiếng của Việt Nam.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Kh&aacute;m ph&aacute; T&acirc;y Nguy&ecirc;n những m&ugrave;a hoa</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Th&aacute;ng 12 l&agrave; thời điểm l&yacute; tưởng để du kh&aacute;ch đến T&acirc;y Nguy&ecirc;n bởi diễn ra nhiều lễ hội v&agrave; l&agrave; m&ugrave;a hoa d&atilde; quỳ nở v&agrave;ng rực tr&ecirc;n đất đỏ bazan.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Đầu th&aacute;ng 1, cả T&acirc;y Nguy&ecirc;n chuyển m&igrave;nh trong m&agrave;u l&aacute; của những c&aacute;nh rừng cao su. Nh&igrave;n ngắm khung cảnh c&aacute;c h&agrave;ng c&acirc;y cao su tr&uacute;t l&aacute;, bạn sẽ thấy T&acirc;y Nguy&ecirc;n hiện l&ecirc;n thật kh&aacute;c lạ.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\">Th&aacute;ng 3, những rẫy hoa cà ph&ecirc; trắng muốt bắt đầu nở v&agrave;o mỗi dịp xu&acirc;n về. Mỗi vụ hoa thường nở 2 &ndash; 3 đợt cho đến cuối m&ugrave;a xu&acirc;n.</span></span></p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(27, 27, 27); font-family:arial; font-size:14px; line-height:normal; margin:0px auto 10px; max-width:100%; padding:0px; text-align:justify; vertical-align:baseline; width:470px; zoom:1\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"Rừng cao su thay lá tuyệt đẹp ở Gia Lai. Ảnh: phuot.vn\" src=\"http://www.dulich.vn/FileManager/vietpro2203/trai-nghiem-tay-nguyen-ivivu-c-4539-4822-1449111777.jpg\" style=\"margin:0px; width:470px\" /></span></span>\r\n\r\n			<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n			</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<p style=\"text-align:center\"><em><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\">Rừng cao su thay l&aacute; tuyệt đẹp ở Gia Lai. Ảnh:<em>&nbsp;phuot</em></span></span></span></em></p>\r\n\r\n			<div>&nbsp;</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n',1,'2016-04-08 22:00:30','2016-04-08 22:00:30',NULL,1,0),(5,'Kinh nghiệm du lịch Côn Đảo',NULL,'<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Nổi tiếng l&agrave; di t&iacute;ch lịch sử gắn liền với qu&aacute; khứ đau thương của d&acirc;n tộc, khi nhắc đến du lịch C&ocirc;n Đảo, nhiều người vẫn chỉ nghĩ đến những nh&agrave; t&ugrave; đ&aacute;ng sợ. Tuy nhi&ecirc;n c&aacute;c năm gần đ&acirc;y, khi du lịch biển đảo th&agrave;nh xu hướng mới, người ta đ&atilde; ph&aacute;t hiện ra vẻ nguy&ecirc;n sơ v&agrave; kỳ vĩ của v&ugrave;ng đảo xinh đẹp thuộc B&agrave; Rịa -Vũng T&agrave;u. Biển xanh trong, c&aacute;t trắng mịn, kh&iacute; trời m&aacute;t rười rượi đ&atilde; khiến du lịch C&ocirc;n Đảo trở n&ecirc;n hấp dẫn kh&ocirc;ng chỉ với du kh&aacute;ch trong nước m&agrave; c&ograve;n với người nước ngo&agrave;i.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"\" src=\"http://www.dulich.vn/FileManager/Folder-ThuHuong/Du-lich-con-dao-01.jpg\" style=\"height:350px; margin:0px; width:540px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><strong><span style=\"font-family:arial,helvetica,sans-serif\">C&ocirc;n Đảo - Biển trong xanh, c&aacute;t trắng mịn</span></strong></em></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Di chuyển: phương tiện, di chuyển khi du lịch C&ocirc;n Đảo</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>M&aacute;y bay</strong>: Hiện tại c&oacute; 2 h&atilde;ng h&agrave;ng kh&ocirc;ng c&oacute; chuyến bay phục vụ h&agrave;nh kh&aacute;ch muốn du lịch C&ocirc;n Đảo l&agrave; Vietnam Airlines v&agrave; Mekong Air. G&iacute;a v&eacute; từ 1-1,5 triệu/chiều. Từ s&acirc;n bay Cỏ Ống ở C&ocirc;n Đảo v&agrave;o trung t&acirc;m khoảng 12 km. Bạn c&oacute; thể thu&ecirc; taxi hoặc đăng k&yacute; dịch vụ đưa đ&oacute;n trước với kh&aacute;ch sạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>T&agrave;u</strong>: Ở Vũng T&agrave;u h&agrave;ng ng&agrave;y c&oacute; t&agrave;u ra du lịch C&ocirc;n Đảo, gi&aacute; v&eacute; từ 200-300 ngh&igrave;n đồng, tuy nhi&ecirc;n thời gian di chuyển kh&aacute; l&acirc;u (tầm 11-12h), bạn n&ecirc;n c&acirc;n nhắc trước xem m&igrave;nh c&oacute; say s&oacute;ng hay sức khỏe yếu kh&ocirc;ng. Từ S&agrave;i G&ograve;n đến Vũng T&agrave;u c&oacute; thể chọn đi xe hoặc t&agrave;u c&aacute;nh ngầm, gi&aacute; v&eacute; xe từ 100-120K, gi&aacute; t&agrave;u c&aacute;nh ngầm khoảng 200-250K t&ugrave;y ng&agrave;y. Nếu chọn t&agrave;u c&aacute;nh ngầm n&ecirc;n mua v&eacute; của h&atilde;ng c&oacute; t&agrave;u to để giảm bớt mệt mỏi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Ở C&ocirc;n Đảo</strong>: Du lịch C&ocirc;n Đảo c&oacute; lợi thế kh&ocirc;ng kh&iacute; m&aacute;t l&agrave;nh, đường rộng vắng, thi&ecirc;n nhi&ecirc;n l&atilde;ng mạn, rất th&iacute;ch hợp để thưởng ngoạn bằng v&agrave;i v&ograve;ng dạo xe m&aacute;y.&nbsp;C&oacute; thể thu&ecirc; xe tại kh&aacute;ch sạn hay c&aacute;c điểm thu&ecirc; xe m&aacute;y, gi&aacute; từ 100.000đ &ndash; 150.000đ/xe/ng&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"\" src=\"http://www.dulich.vn/FileManager/Folder-ThuHuong/du-lich-con-dao-02.jpg\" style=\"height:281px; margin:0px; width:500px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif\"><em><strong>C&ocirc;n Đảo nh&igrave;n từ tr&ecirc;n cao</strong></em></span></p>\r\n<span style=\"font-size:16px\">Tham quan: Di t&iacute;ch, b&atilde;i biển, điểm tham quan của du lịch C&ocirc;n Đảo</span>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>TẮM BIỂN</strong>:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Biển ở C&ocirc;n Đảo nước trong veo, gi&oacute; l&ugrave;a m&aacute;t rười rượi, xung quanh b&atilde;i tắm l&agrave; những h&agrave;ng c&acirc;y xanh ngắt. Một số b&atilde;i tắm của du lịch C&ocirc;n Đảo bạn n&ecirc;n gh&eacute; qua như:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>B&atilde;i Đầm Trầu</strong>, gần s&acirc;n bay Cỏ Ống: c&aacute;t trắng xốp mịn, n&uacute;i &ocirc;m lấy biển theo h&igrave;nh v&ograve;ng cung n&ecirc;n b&atilde;i Đầm Trầu lu&ocirc;n c&oacute; s&oacute;ng nhẹ, y&ecirc;n tĩnh. Đ&acirc;y được xem l&agrave; b&atilde;i tắm đẹp nhất của du lịch C&ocirc;n Đảo.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><em><strong><img alt=\"\" src=\"http://www.dulich.vn/FileManager/Folder-ThuHuong/du-lich-con-dao-03.jpg\" style=\"height:264px; margin:0px; width:500px\" /></strong></em>\r\n\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; background: transparent;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif\"><em><strong>B&atilde;i Đầm Trầu - C&ocirc;n Đảo</strong></em></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong>B&atilde;i L&ograve; V&ocirc;i</strong>&nbsp;dọc đường T&ocirc;n Đức Thắng: th&iacute;ch hợp cho gia đ&igrave;nh hoặc nh&oacute;m đ&ocirc;ng người.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>B&atilde;i An Hải</strong>: c&aacute;ch trung t&acirc;m huyện chỉ 10 ph&uacute;t đi bộ, được bao quanh bởi n&uacute;i n&ecirc;n rất &ecirc;m dịu v&agrave; phẳng lặng, nước trong xanh.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>B&atilde;i Đất Dốc</strong>: c&aacute;c hẻm n&uacute;i ăn s&acirc;u v&agrave;o bở biển tạo ra c&aacute;c b&atilde;i biển nhỏ xinh đẹp v&agrave; y&ecirc;n tĩnh.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>NGẮM MẶT TRỜI</strong>: Du lịch C&ocirc;n Đảo cho bạn cơ hội ngắm mặt trời tuyệt vời nhất. Thức dậy thật sớm để đ&oacute;n b&igrave;nh minh tại Mũi C&aacute; Mập, mặt trời mọc qua h&ograve;n Bảy Cạnh, c&aacute;c đ&aacute;m m&acirc;y tr&ecirc;n bầu trời c&ugrave;ng với tia nắng ban mai tạo n&ecirc;n một m&agrave;n tr&igrave;nh diễn &aacute;nh s&aacute;ng kỳ ảo. Chiều đến dạo qua b&atilde;i Nh&aacute;t ngắm nh&igrave;n ho&agrave;ng h&ocirc;n ấm &aacute;p từ từ lặn qua Đỉnh T&igrave;nh Y&ecirc;u.</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"\" src=\"http://www.dulich.vn/FileManager/Folder-ThuHuong/binh_minh_mui_Ca_map.jpg\" style=\"height:348px; margin:0px; width:500px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><strong>B&igrave;nh minh Mũi C&aacute; Mập</strong></em></p>\r\n\r\n<p style=\"text-align:justify\"><strong>THĂM TH&Uacute; THI&Ecirc;N NHI&Ecirc;N:</strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Rừng nguy&ecirc;n sinh &Ocirc;ng Đụng</strong>: Từ C&ocirc;n Đảo, bạn c&oacute; thể đi về ph&iacute;a T&acirc;y, trải nghiệm chuyến trekking 20 ph&uacute;t, băng qua rừng nguy&ecirc;n sinh &Ocirc;ng Đụng. Những h&agrave;ng c&acirc;y nhiệt đới xanh m&aacute;t, hoang sơ dẫn đến b&atilde;i biển xinh đẹp cuối rừng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>H&ograve;n Bảy Cạnh</strong>: l&ecirc;n t&agrave;u đi tham quan h&ograve;n Bảy cạnh,&nbsp;h&ograve;n đảo lớn thứ hai trong số 16 h&ograve;n đảo thuộc quần đảo C&ocirc;n Đảo. To&agrave;n bộ đảo được che phủ bởi rừng nhiệt đới nguy&ecirc;n sinh. Đến đ&acirc;y ngo&agrave;i tham quan, bạn c&oacute; thể đăng k&yacute; tour lặn ngắm san h&ocirc; hấp dẫn.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"\" src=\"http://www.dulich.vn/FileManager/Folder-ThuHuong/du-lich-lan-ngam-san-ho-phu-quoc_du-lich-viet.jpg\" style=\"height:304px; margin:0px; width:500px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif\"><em><strong>Lặn ngắm San h&ocirc; C&ocirc;n Đảo</strong></em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>H&ograve;n Tre</strong>&nbsp;cũng l&agrave; địa điểm tham quan hấp dẫn của du lịch C&ocirc;n Đảo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Vịnh Đầm Tre</strong>&nbsp;với cảnh quan tự nhi&ecirc;n, k&iacute;n gi&oacute;, c&oacute; rừng ngập mặn bao bọc xung quanh. Tại đ&acirc;y, du kh&aacute;ch c&oacute; thể tham quan rừng ngập mặn, bơi lặn ngắm san h&ocirc;, xem chim yến l&agrave;m tổ trong m&ugrave;a sinh sản v&agrave; nhiều lo&agrave;i sinh vật biển kh&aacute;. Tr&ecirc;n đường đi tha hồ ngắm cảnh đẹp của vịnh C&ocirc;n Sơn, mũi L&ograve; V&ocirc;i, b&atilde;i biển Đất Dốc, mũi Chim Chim, vịnh Đ&ocirc;ng Bắc&hellip;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>T&Igrave;M HIỂU LỊCH SỬ:&nbsp;</strong>Đến với du lịch C&ocirc;n Đảo kh&ocirc;ng thể bỏ qua lịch sử h&agrave;o h&ugrave;ng của v&ugrave;ng đất n&agrave;y th&ocirc;ng qua c&aacute;c di t&iacute;ch &yacute; nghĩa:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Bảo T&agrave;ng C&ocirc;n Đảo</strong>: Trước đ&acirc;y l&agrave; nơi ở v&agrave; l&agrave;m việc của c&aacute;c đời ch&uacute;a đảo. Đến với bảo t&agrave;ng, bạn c&oacute; cơ hội t&igrave;m hiểu lịch sử h&igrave;nh th&agrave;nh C&ocirc;n Đảo th&ocirc;ng qua c&aacute;c hiện vật, cổ vật, h&igrave;nh ảnh v&agrave; c&aacute;c tư liệu từ thời Ph&aacute;p thuộc đến nay.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Trại t&ugrave; Ph&uacute; Sơn, Ph&uacute; Hải C&ocirc;n Đảo</strong>: Nơi giam giữ c&aacute;c t&ugrave; binh cộng sản thời Ph&aacute;p v&agrave; Mỹ.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"\" src=\"http://www.dulich.vn/FileManager/Folder-ThuHuong/du-lich-con-dao-04.jpg\" style=\"height:375px; margin:0px; width:500px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif\"><em><strong>Trại t&ugrave; Ph&uacute; Sơn - C&ocirc;n Đảo</strong></em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Khu di t&iacute;ch Chuồng Cọp</strong>: T&igrave;m hiểu cuộc sống, c&aacute;c c&aacute;ch tra tấn d&atilde; man, nơi sinh hoạt của c&aacute;c t&ugrave; binh bị giam cầm, đ&acirc;y cũng l&agrave; nơi giam giữ chị V&otilde; Thị S&aacute;u.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Nghĩa trang H&agrave;ng Dương</strong>&nbsp;nơi c&oacute; mộ của hơn 2000 liệt sĩ v&agrave; mộ V&otilde; Thị S&aacute;u l&agrave; điểm kh&ocirc;ng thể bỏ qua. Đến đ&acirc;y ngo&agrave;i thắp hương, bạn n&ecirc;n chuẩn bị đồ lễ, kh&ocirc;ng thể thiếu gương lược. Nếu mang được hoa tươi th&igrave; c&agrave;ng tốt, n&ecirc;n d&ugrave;ng hoa trắng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>DU LỊCH T&Acirc;M LINH:&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Đền thờ b&agrave; Phi Yến</strong>&nbsp;hay c&ograve;n gọi l&agrave;&nbsp;<strong>An Sơn Miếu</strong>: B&agrave; Phi Yến l&agrave; vợ vua Nguyễn &Aacute;nh, mẹ của Ho&agrave;ng tử Cải. Khi bị kẻ xấu lợi dụng, v&igrave; qu&aacute; uất ức, b&agrave; Phi Yến đ&atilde; tự vẫn khi chỉ mới 25 tuổi.&nbsp;Bạn cũng n&ecirc;n viếng thăm Miếu v&agrave; mộ của Ho&agrave;ng tử Cải, ngay gần s&acirc;n bay Cỏ Ống.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"\" src=\"http://www.dulich.vn/FileManager/Folder-ThuHuong/du-lich-con-dao-05.jpg\" style=\"height:333px; margin:0px; width:500px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif\"><em><strong>Đền thờ B&agrave; Phi Yến</strong></em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Ch&ugrave;a N&uacute;i Một</strong>&nbsp;nằm ở trung t&acirc;m huyện được xem l&agrave; ng&ocirc;i ch&ugrave;a c&oacute; phong thủy đẹp nhất Việt Nam. Ch&ugrave;a mới kh&aacute;nh th&agrave;nh năm 2011, nằm tr&ecirc;n n&uacute;i, &nbsp;c&oacute; tầm nh&igrave;n bao qu&aacute;t xuống biển &ndash; n&uacute;i &ndash; th&agrave;nh phố &ndash; hồ sen An Hải v&ocirc; c&ugrave;ng đẹp.</span></p>\r\n<span style=\"font-size:16px\">Ăn: Qu&aacute;n ăn, ăn vặt, đặc sản của du lịch C&ocirc;n Đảo</span>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Nh&igrave;n chung, ở C&ocirc;n Đảo kh&ocirc;ng c&oacute; nhiều nh&agrave; h&agrave;ng, qu&aacute;n ăn ngo&agrave;i nh&agrave; h&agrave;ng trong c&aacute;c kh&aacute;ch sạn hay resort, nhưng c&aacute;c qu&aacute;n ăn ở đ&acirc;y c&oacute; gi&aacute; tương đương nhau v&agrave; nấu nướng kh&aacute; ngon. Nổi tiếng c&oacute; qu&aacute;n Tri Kỷ nằm tr&ecirc;n đường Nguyễn Đức Nhuận, b&aacute;n hải sản v&agrave; c&aacute;c m&oacute;n cơm. Hoặc &nbsp;Qu&aacute;n Thu Ba ngay chợ C&ocirc;n Đảo, tr&ecirc;n đường V&otilde; Thị S&aacute;u: qu&aacute;n nhỏ nhắn, sạch sẽ, b&agrave; chủ rất kỹ t&iacute;nh v&agrave; n&ecirc;m nếm rất ngon, b&agrave;n ghế v&agrave; ch&eacute;n đũa rất tươm tất. Qu&aacute;n b&aacute;n c&aacute;c loại hải sản, m&oacute;n ăn cơm, m&oacute;n lẩu ch&aacute;o&hellip;Qu&aacute;n Phương Hạnh nằm trong hẻm nhỏ, dưới t&agrave;ng c&acirc;y khế m&aacute;t rượi,&hellip;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Một số đặc sản hấp dẫn của du lịch C&ocirc;n Đảo c&oacute; thể kể đến</strong>:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Ốc V&uacute; N&agrave;ng:&nbsp;</strong>Những con ốc h&igrave;nh ch&oacute;p, ngọt thịt, thơm ngon d&ugrave; nuớng, luộc, x&agrave;o, l&agrave;m gỏi,&hellip; ngon nhất l&agrave; ốc V&uacute; N&agrave;ng nướng mỡ h&agrave;nh. C&aacute;c qu&aacute;n ăn ở đ&acirc;y đều phục vụ đặc sản n&agrave;y. Bạn c&oacute; c&oacute; thể vừa thưởng thức hải sản vừa ngắm biển xanh ngắt.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><img alt=\"\" src=\"http://www.dulich.vn/FileManager/Folder-ThuHuong/du-lich-con-dao-06.jpg\" style=\"height:333px; margin:0px; width:500px\" />\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif\"><em><strong>Ốc V&uacute; n&agrave;ng - Đặc sản C&ocirc;n Đảo</strong></em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>C&aacute; m&uacute; đỏ:&nbsp;</strong>Đặc sản v&ugrave;ng biển đảo. Thịt c&aacute; dai, ngọt v&agrave; thơm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>T&ocirc;m h&ugrave;m v&agrave; T&ocirc;m h&ugrave;m mũ ni:&nbsp;</strong>T&ocirc;m h&ugrave;m được xem l&agrave; &ldquo;vua hải sản&rdquo; ở C&ocirc;n Đảo, tuy kh&ocirc;ng rẻ hơn trong đất liền bao nhi&ecirc;u nhưng t&ocirc;m ở đ&acirc;y tươi ngon đặc biệt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Mứt hạt b&agrave;ng</strong>: C&acirc;y b&agrave;ng mọc khắp nơi tr&ecirc;n C&ocirc;n Đảo. Quả b&agrave;ng ch&iacute;n v&agrave;ng ươm, b&oacute;c lớp thịt lộ ra hạt trắng n&otilde;n. Hạt b&agrave;ng ở C&ocirc;n Đảo đợc rang mặn với muối hay rang ngọt c&ugrave;ng đường. Ăn b&ugrave;i, thơm gi&ograve;n. Hẩu như ai đến du lịch C&ocirc;n Đảo cũng mua v&agrave;i c&acirc;n mứt hạt b&agrave;ng về l&agrave;m qu&agrave;. G&iacute;a tầm 20-30 ngh&igrave;n/kg. M&ugrave;a cao điểm c&oacute; thể l&ecirc;n đến 50 ngh&igrave;n/kg.<br />\r\n<strong>Mắm h&agrave;u:</strong>&nbsp;M&oacute;n nước chấm kh&ocirc;ng thể thiếu trong bữa ăn ở C&ocirc;n Đảo. L&agrave;m từ thịt h&agrave;u trắng tươi, trộn với muối, ớt, gia vị,&hellip; trong nhiều ng&agrave;y, lắng ra loại nước thơm lừng m&agrave;u n&acirc;u đỏ l&agrave;m n&ecirc;n sự độc đ&aacute;o cho c&aacute;c m&oacute;n ăn ở đ&acirc;y.</span></p>\r\n',1,'2016-04-08 22:03:13','2016-04-08 22:03:13',NULL,1,0),(6,'Ăn phở ở Hà Nội là \'trải nghiệm nên có trong đời\'',NULL,'<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 12px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; color: rgb(27, 27, 27); font-family: \'Open Sans\', sans-serif; line-height: 18.4px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"color:rgb(0, 0, 0); font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"><span style=\"font-size:14px\"><strong>Bạn c&oacute; biết rằng, mỗi buổi s&aacute;ng thức dậy, thưởng thức một t&ocirc; phở giữa l&ograve;ng thủ đ&ocirc;, l&agrave; bạn đ&atilde; thực hiện được một trong 100 trải nghiệm n&ecirc;n c&oacute; trong đời?</strong></span></span></span><br />\r\n&nbsp;</div>\r\n\r\n<div id=\"article-body\" style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; color: rgb(0, 0, 0); font-family: \'Segoe UI\', Arial, \'Helvetica Neue\', Helvetica, sans-serif; line-height: 21.92px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\">Trong tuần qua, tạp ch&iacute; Business Insider (Mỹ) đ&atilde; đưa ra danh s&aacute;ch 100 chuyến h&agrave;nh tr&igrave;nh n&ecirc;n thực hiện trong đời. Đ&iacute;ch đến của những chuyến h&agrave;nh tr&igrave;nh n&agrave;y l&agrave; những địa danh nhận được nhiều sự quan t&acirc;m của độc giả trong suốt nhiều năm qua v&agrave; hứa hẹn sẽ tiếp tục l&agrave; những điểm đến hấp dẫn trong những năm tới.</span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><em>Trong danh s&aacute;ch 100 chuyến đi n&agrave;y, c&oacute; chuyến đi tới H&agrave; Nội để thử nếm m&oacute;n phở trứ danh:</em></span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; vertical-align: baseline; background: transparent;\">\r\n<div class=\"article-photo inlinephoto\" style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; background: transparent;\">\r\n<div class=\"wrap_img\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: justify; vertical-align: baseline; position: relative; display: inline-block; background: transparent;\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"http://www.dulich.vn/FileManager/vietpro2203/1_CHIZ-Copy-1.jpg\" style=\"margin:0px; width:440px\" /></span>\r\n\r\n<div class=\"alt\" style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; text-align: center; vertical-align: baseline; font-style: italic; background: transparent;\">&nbsp;</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"article-photo inlinephoto\" style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; border: 0px; outline: 0px; text-shadow: rgba(0, 0, 0, 0.00784314) 0px 0px 0px; vertical-align: baseline; background: transparent;\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>Tạp ch&iacute; Business Insider khuy&ecirc;n độc giả n&ecirc;n thử một lần trong đời tới H&agrave; Nội để ăn phở. Đương nhi&ecirc;n, đ&oacute; chỉ l&agrave; một n&eacute;t đặc biệt được &ldquo;lẩy ra&rdquo; trong đời sống văn h&oacute;a - ẩm thực phong ph&uacute; của đất H&agrave; Th&agrave;nh, bởi một khi đ&atilde; đặt ch&acirc;n tới th&agrave;nh phố ngh&igrave;n năm tuổi, du kh&aacute;ch sẽ c&oacute; nhiều điều kh&aacute;c nữa để kh&aacute;m ph&aacute;, chi&ecirc;m ngưỡng v&agrave; bất ngờ.</em></span></div>\r\n\r\n<div class=\"article-photo inlinephoto\" style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; border: 0px; outline: 0px; text-shadow: rgb